import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css"; // Assuming you have some basic styles in App.css

export default function Profile({ token }) {
  const [userData, setUserData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    age: "",
    height: "",
    weight: "",
    chronic_diseases: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(res.data);
      } catch (err) {
        console.error("Profil bilgileri alƒ±namadƒ±:", err.response?.data || err.message);
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    setLoading(true);
    setMessage("");
    try {
      await axios.post("http://127.0.0.1:5000/profile/update", userData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage("‚úÖ Profil ba≈üarƒ±yla g√ºncellendi!");
    } catch (err) {
      setMessage("‚ö†Ô∏è Profil g√ºncellenemedi.");
      console.error(err.response?.data || err.message);
    }
    setLoading(false);
  };

  return (
    <div className="profile-container">
      <h2 className="profile-title">üë§ Profil Bilgileri</h2>
      <div className="profile-grid">
        {Object.keys(userData).map((key) => (
          <div className="profile-field" key={key}>
            <label>{key.replace("_", " ").toUpperCase()}:</label>
            <input
              type="text"
              name={key}
              value={userData[key] || ""}
              onChange={handleChange}
            />
          </div>
        ))}
      </div>
      <button onClick={handleSave} disabled={loading} style={{ marginTop: "20px" }}>
        {loading ? "Kaydediliyor..." : "Kaydet"}
      </button>
      {message && <p style={{ marginTop: "10px" }}>{message}</p>}
    </div>
  );
}
