import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "./App.css";

export default function ChatBox({ token }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [labFile, setLabFile] = useState(null);
  const [labAnalysis, setLabAnalysis] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/history", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMessages(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchHistory();
  }, [token]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, labAnalysis]);

  const handleSend = async () => {
    if (!input.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post(
        "http://127.0.0.1:5000/chat",
        { message: input },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessages(prev => [...prev, { sender: "user", text: input }, { sender: "bot", text: res.data.reply }]);
      setInput("");
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const handleLabUpload = async () => {
    if (!labFile) return;
    const formData = new FormData();
    formData.append("file", labFile);
    try {
      const res = await axios.post(
        "http://127.0.0.1:5000/lab_upload",
        formData,
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } }
      );
      setLabAnalysis(res.data.analysis);
      setMessages(prev => [...prev, { sender: "bot", text: `ðŸ“„ ${labFile.name} analiz sonucu:\n${res.data.analysis}` }]);
      setLabFile(null);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-card chat-container">
        <div className="chat-messages">
          {messages.map((msg, i) => (
            <div key={i} className={`message ${msg.sender}`}>
              {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="chat-input-area">
          <textarea
            rows="3"
            placeholder="MesajÄ±nÄ±zÄ± yazÄ±n..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={handleSend} disabled={loading} className="send-btn">
            {loading ? "GÃ¶nderiliyor..." : "GÃ¶nder"}
          </button>

          <div className="lab-section">
            <input type="file" onChange={e => setLabFile(e.target.files[0])} />
            <button onClick={handleLabUpload} disabled={!labFile} className="upload-btn">
              Tahlil Ekle & Analiz Et
            </button>
            {labAnalysis && <p className="chat-message">ðŸ“„ Analiz: {labAnalysis}</p>}
          </div>
        </div>
      </div>
    </div>
  );
}
