import React, { useState, useEffect } from "react";
import AuthForm from "./components/AuthForm";
import ChatBox from "./ChatBox";
import "./App.css";
import axios from "axios";

function App() {
  const [formType, setFormType] = useState("login");
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const [messageColor, setMessageColor] = useState("green");
  const [token, setToken] = useState(localStorage.getItem("token"));

  // token değişirse localStorage güncellensin
  useEffect(() => {
    if (token) {
      localStorage.setItem("token", token);
    } else {
      localStorage.removeItem("token");
    }
  }, [token]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const res = await axios.post("http://127.0.0.1:5000/login", {
        email: formData.email,
        password: formData.password
      });
      setMessageColor("green");
      setMessage("Giriş başarılı!");
      console.log("JWT Token:", res.data.token);

      setToken(res.data.token); // ✅ ChatBox’a geçişi sağlar
    } catch (err) {
      setMessageColor("red");
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
      setMessageColor("red");
      setMessage("Tüm alanlar zorunludur");
      return;
    }

    try {
      const payload = {
        first_name: formData.firstName,
        last_name: formData.lastName,
        email: formData.email,
        password: formData.password
      };

      console.log("Gönderilen veri:", payload);
      await axios.post("http://127.0.0.1:5000/register", payload);

      setMessageColor("green");
      setMessage("Kayıt başarılı! Giriş yapabilirsiniz.");

      setFormData({ firstName: "", lastName: "", email: "", password: "" });
      setFormType("login");
    } catch (err) {
      setMessageColor("red");
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const toggleForm = () => {
    setFormType(formType === "login" ? "register" : "login");
    setMessage("");
    setFormData({ firstName: "", lastName: "", email: "", password: "" });
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div className="app-container">
      <h1 className="app-title">EMMIChat</h1>

      {token ? (
        <>
          <button
            onClick={handleLogout}
            style={{ position: "absolute", top: 10, right: 10 }}
          >
            Çıkış Yap
          </button>
          <ChatBox token={token} />
        </>
      ) : (
        <>
          <h2 className="app-subtitle">
            {formType === "login" ? "Giriş Yap" : "Kayıt Ol"}
          </h2>

          <AuthForm
            type={formType}
            formData={formData}
            setFormData={setFormData}
            onSubmit={formType === "login" ? handleLogin : handleRegister}
            toggleForm={toggleForm}
          />

          {message && (
            <p style={{ marginTop: "10px", color: messageColor }}>{message}</p>
          )}
        </>
      )}
    </div>
  );
}

export default App;
