import React, { useState } from "react";
import AuthForm from "./components/AuthForm";
import "./App.css";
import axios from "axios";

function App() {
  const [formType, setFormType] = useState("login");
  const [formData, setFormData] = useState({ firstName: "", lastName: "", email: "", password: "" });
  const [message, setMessage] = useState("");

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://127.0.0.1:5000/login", {
        email: formData.email,
        password: formData.password
      });
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post("http://127.0.0.1:5000/register", formData);
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const toggleForm = () => setFormType(formType === "login" ? "register" : "login");

  return (
    <div className="app-container">
      <h1 className="app-title">EMMIChat</h1>
      <h2 className="app-subtitle">{formType === "login" ? "Giriş Yap" : "Kayıt Ol"}</h2>

      <AuthForm
        type={formType}
        formData={formData}
        setFormData={setFormData}
        onSubmit={formType === "login" ? handleLogin : handleRegister}
        toggleForm={toggleForm}
      />

      {message && <p style={{ marginTop: "10px", color: "green" }}>{message}</p>}
    </div>
  );
}

export default App;
