import React, { useState } from "react";
import axios from "axios";
import AuthForm from "./AuthForm";

export default function App() {
  const [type, setType] = useState("login"); // login veya register
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: ""
  });
  const [token, setToken] = useState("");
  const [message, setMessage] = useState("");

  const toggleForm = () => {
    setType(type === "login" ? "register" : "login");
    setFormData({ firstName: "", lastName: "", email: "", password: "" });
    setMessage("");
  };

  const handleSubmit = async (e) => {
    console.log({
  first_name: formData.firstName,
  last_name: formData.lastName,
  email: formData.email,
  password: formData.password
});

    e.preventDefault();
    setMessage("");

    if (type === "register") {
      if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
        setMessage("Tüm alanlar zorunludur");
        return;
      }

      try {
        await axios.post("http://127.0.0.1:5000/register", {
          first_name: formData.firstName,
          last_name: formData.lastName,
          email: formData.email,
          password: formData.password
        });
        setMessage("Kayıt başarılı! Giriş yapabilirsiniz.");
        setFormData({ firstName: "", lastName: "", email: "", password: "" });
        setType("login");
      } catch (err) {
        setMessage(err.response?.data?.error || "Kayıt sırasında hata oluştu");
      }
    } else {
      if (!formData.email || !formData.password) {
        setMessage("Email ve şifre zorunludur");
        return;
      }

      try {
        const res = await axios.post("http://127.0.0.1:5000/login", {
          email: formData.email,
          password: formData.password
        });
        localStorage.setItem("token", res.data.token);
        setToken(res.data.token);
        setMessage("Giriş başarılı!");
        setFormData({ firstName: "", lastName: "", email: "", password: "" });
      } catch (err) {
        setMessage(err.response?.data?.error || "Giriş sırasında hata oluştu");
      }
    }
  };

  return (
    <div className="App">
      <h1>{type === "login" ? "Giriş Yap" : "Kayıt Ol"}</h1>
      <AuthForm
        type={type}
        formData={formData}
        setFormData={setFormData}
        onSubmit={handleSubmit}
        toggleForm={toggleForm}
      />
      {message && <p style={{ color: type === "login" ? "red" : "green" }}>{message}</p>}
      {token && <p>JWT Token: {token}</p>}
    </div>
  );
}
