import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

export default function Profile({ token }) {
  const [userData, setUserData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    age: "",
    height: "",
    weight: "",
    chronic_diseases: ""
  });

  const [labResults, setLabResults] = useState([
    { name: "Kan Şekeri", value: "90 mg/dL" },
    { name: "Kolesterol", value: "180 mg/dL" },
    { name: "Tansiyon", value: "120/80 mmHg" }
  ]);

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(res.data);
      } catch (err) {
        console.error("Profil bilgileri alınamadı:", err.response?.data || err.message);
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    setLoading(true);
    setMessage("");
    try {
      await axios.post("http://127.0.0.1:5000/profile", userData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage("✅ Profil başarıyla güncellendi!");
    } catch (err) {
      setMessage("⚠️ Profil güncellenemedi.");
      console.error(err.response?.data || err.message);
    }
    setLoading(false);
  };

  return (
    <div className="profile-page">
      <div className="profile-container">
        {/* Sol taraf - Tahlil Sonuçları */}
        <div className="lab-results">
          <h3>Tahlil Sonuçları 🧪</h3>
          <ul>
            {labResults.map((res, index) => (
              <li key={index}>
                <strong>{res.name}:</strong> {res.value}
              </li>
            ))}
          </ul>
        </div>

        {/* Sağ taraf - Profil Bilgileri */}
        <div className="profile-card">
          <h2 className="profile-title">👤 Profil Bilgileri</h2>

          <div className="profile-fields">
            <div className="profile-field">
              <label>Ad</label>
              <input type="text" name="firstname" value={userData.firstname} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Soyad</label>
              <input type="text" name="lastname" value={userData.lastname} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Kullanıcı Adı / E-posta</label>
              <input type="text" name="username" value={userData.username} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Yaş</label>
              <input type="number" name="age" value={userData.age} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Boy (cm)</label>
              <input type="number" name="height" value={userData.height} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Kilo (kg)</label>
              <input type="number" name="weight" value={userData.weight} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Kronik Rahatsızlıklar</label>
              <textarea
                name="chronic_diseases"
                value={userData.chronic_diseases}
                onChange={handleChange}
                rows="3"
              />
            </div>
          </div>

          <button onClick={handleSave} disabled={loading} className="save-btn">
            {loading ? "Kaydediliyor..." : "Kaydet"}
          </button>
          {message && <p className="profile-message">{message}</p>}
        </div>
      </div>
    </div>
  );
}
