import React, { useState } from "react";
import ChatBox from "./ChatBox";
import Login from "./Login";
import Register from "./Register";
import Profile from "./Profile"; // Profil componenti
import "./App.css";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [showRegister, setShowRegister] = useState(false);
  const [view, setView] = useState("chat"); // chat veya profile

  return (
    <div className="app">
      {/* Başlık: Form kutusunun dışında */}
      {!token && (
        <>
          <h1 className="app-title">CHATDOC 🩺</h1>
          <h2 className="app-subtitle">Sağlığınız için AI.</h2>
        </>
      )}

      {!token ? (
        <>
          {showRegister ? (
            <>
              <Register setToken={setToken} />
              <div className="form-toggle-wrapper">
                <p>Zaten üyeyim?</p>
                <button onClick={() => setShowRegister(false)}>Giriş Yap</button>
              </div>
            </>
          ) : (
            <>
              <Login setToken={setToken} />
              <div className="form-toggle-wrapper">
                <p>Hesabın yok mu?</p>
                <button onClick={() => setShowRegister(true)}>Kayıt Ol</button>
              </div>
            </>
          )}
        </>
      ) : (
        <>
          {/* Chat veya Profil ekranı başlığı */}
          <h1 className="app-title">CHATDOC 🩺</h1>
          <h2 className="app-subtitle">Sağlığınız için AI.</h2>

          {/* Chat / Profil Geçiş Butonları */}
          <div className="form-toggle-wrapper">
            <button onClick={() => setView("chat")}>Chat</button>
            <button onClick={() => setView("profile")}>Profil</button>
            <button
              onClick={() => {
                localStorage.removeItem("token");
                setToken("");
              }}
            >
              Çıkış Yap
            </button>
          </div>

          {/* Ekran gösterimi */}
          {view === "chat" && <ChatBox token={token} />}
          {view === "profile" && <Profile token={token} />}
        </>
      )}
    </div>
  );
}

export default App;
