import React, { useState } from "react";
import axios from "axios";

export default function Register() {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault(); // Sayfa reload engelle
    setError("");
    setSuccess("");

    if (!firstname || !lastname || !email || !password) {
      setError("Tüm alanlar zorunludur");
      return;
    }

    try {
      const payload = {
        firstname,
        lastname,
        username: email, // backend username olarak email'i alıyor
        password
      };

      console.log("Gönderilen veri:", payload);

      await axios.post("http://127.0.0.1:5000/register", payload);

      setSuccess("Kayıt başarılı! Giriş yapabilirsiniz.");

      // Formu temizle
      setFirstname("");
      setLastname("");
      setEmail("");
      setPassword("");

    } catch (err) {
      console.log(err.response?.data);
      setError(err.response?.data?.error || "Kayıt sırasında hata oluştu");
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input
        type="text"
        placeholder="Ad"
        value={firstname}
        onChange={e => setFirstname(e.target.value)}
      />
      <input
        type="text"
        placeholder="Soyad"
        value={lastname}
        onChange={e => setLastname(e.target.value)}
      />
      <input
        type="email"
        placeholder="E-posta"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Şifre"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />
      <button type="submit">Kayıt Ol</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
    </form>
  );
}
