import React, { useState } from "react";
import AuthForm from "./components/AuthForm";
import "./App.css"; // <- CSS'i burada bağladık

function App() {
  const [formType, setFormType] = useState("login"); // login/register toggle
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });

  const handleLogin = () => {
    console.log("Login:", formData);
  };

  const handleRegister = () => {
    console.log("Register:", formData);
  };

  return (
    <div className="app">
      <h1 className="app-title">EMMIChat</h1>
      <h2 className="app-subtitle">Gemini ile Sohbet Et</h2>

      <AuthForm
        type={formType}
        formData={formData}
        setFormData={setFormData}
        onSubmit={formType === "login" ? handleLogin : handleRegister}
        toggleForm={() =>
          setFormType(formType === "login" ? "register" : "login")
        }
      />
    </div>
  );
}

export default App;
