import React, { useState } from "react";
import AuthForm from "./components/AuthForm";
import "./App.css";
import axios from "axios";

function App() {
  const [formType, setFormType] = useState("login");
  const [formData, setFormData] = useState({ firstName: "", lastName: "", email: "", password: "" });
  const [message, setMessage] = useState("");
  const [messageColor, setMessageColor] = useState("green"); // Başarı / hata için

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const res = await axios.post("http://127.0.0.1:5000/login", {
        email: formData.email,
        password: formData.password
      });
      setMessageColor("green");
      setMessage("Giriş başarılı!");
      console.log("JWT Token:", res.data.token); // Token logla
    } catch (err) {
      setMessageColor("red");
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
      setMessageColor("red");
      setMessage("Tüm alanlar zorunludur");
      return;
    }

    try {
      const payload = {
        first_name: formData.firstName,
        last_name: formData.lastName,
        email: formData.email,
        password: formData.password
      };

      console.log("Gönderilen veri:", payload); // test için
      const res = await axios.post("http://127.0.0.1:5000/register", payload);

      setMessageColor("green");
      setMessage("Kayıt başarılı! Giriş yapabilirsiniz.");

      // Formu sıfırla ve login formuna geç
      setFormData({ firstName: "", lastName: "", email: "", password: "" });
      setFormType("login");
    } catch (err) {
      setMessageColor("red");
      setMessage(err.response?.data?.error || "Hata oluştu");
    }
  };

  const toggleForm = () => {
    setFormType(formType === "login" ? "register" : "login");
    setMessage("");
    setFormData({ firstName: "", lastName: "", email: "", password: "" });
  };

  return (
    <div className="app-container">
      <h1 className="app-title">EMMIChat</h1>
      <h2 className="app-subtitle">{formType === "login" ? "Giriş Yap" : "Kayıt Ol"}</h2>

      <AuthForm
        type={formType}
        formData={formData}
        setFormData={setFormData}
        onSubmit={formType === "login" ? handleLogin : handleRegister}
        toggleForm={toggleForm}
      />

      {message && <p style={{ marginTop: "10px", color: messageColor }}>{message}</p>}
    </div>
  );
}

export default App;
