import React, { useState } from "react";
import ChatBox from "./ChatBox";
import Profile from "./Profile";
import "./App.css";

export default function App() {
  const [currentPage, setCurrentPage] = useState("auth"); // auth, chat, profile
  const [user, setUser] = useState(null);

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage("chat");
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage("auth");
  };

  const handleGoToProfile = () => {
    setCurrentPage("profile");
  };

  const handleBackToChat = () => {
    setCurrentPage("chat");
  };

  return (
    <div className="app">
      <div className="app-logo">ChatApp</div>

      {currentPage === "auth" && (
        <div>
          <h1 className="app-title">Welcome to ChatApp</h1>
          <h3 className="app-subtitle">Login or Register to start chatting!</h3>
          {/* Burada AuthForm componenti eklenebilir */}
          {/* Örnek kullanım: <AuthForm onLogin={handleLogin} /> */}
        </div>
      )}

      {currentPage === "chat" && user && (
        <div className="chatbox-container">
          <div className="top-right-menu">
            <button onClick={handleGoToProfile}>Profile</button>
            <button onClick={handleLogout}>Logout</button>
          </div>
          <ChatBox token={user.token} />
        </div>
      )}

      {currentPage === "profile" && user && (
        <Profile user={user} onBack={handleBackToChat} />
      )}
    </div>
  );
}
