// src/App.js
import React, { useState } from "react";
import AuthForm from "./components/AuthForm";
import "./App.css";

function App() {
  const [formType, setFormType] = useState("login"); // login veya register
  const [formData, setFormData] = useState({ firstName: "", lastName: "", email: "", password: "" });


  const handleLogin = () => {
    console.log("Login:", formData);
  };

  const handleRegister = () => {
    console.log("Register:", formData);
  };

  return (
    <div className="app-container">
      <h1 className="app-title">EMMIChat</h1>
      <h2 className="app-subtitle">Kullanıcı Girişi</h2>

      <div className="form-toggle">
        <button
          className={formType === "login" ? "active" : ""}
          onClick={() => setFormType("login")}
        >
          Giriş Yap
        </button>
        <button
          className={formType === "register" ? "active" : ""}
          onClick={() => setFormType("register")}
        >
          Kayıt Ol
        </button>
      </div>

      {/* Tek satır değil, mutlaka satır sonu ve indentler düzgün */}
      {formType && (
        <AuthForm
          type={formType}
          formData={formData}
          setFormData={setFormData}
          onSubmit={formType === "login" ? handleLogin : handleRegister}
        />
      )}
    </div>
  );
}

export default App;
