import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css"; // Assuming you have some styles in App.css

export default function Profile({ token }) {
  const [userData, setUserData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    age: "",
    height: "",
    weight: "",
    chronic_diseases: ""
  });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(res.data);
      } catch (err) {
        console.error("Profil bilgileri alÄ±namadÄ±:", err.response?.data || err.message);
      }
    };
    fetchProfile();
  }, [token]);

  return (
    <div className="profile-container">
      <h2 className="profile-title">ðŸ‘¤ Profil Bilgileri</h2>
      <div className="profile-grid">
        <div className="profile-field">
          <label>Ad:</label>
          <input type="text" value={userData.firstname} readOnly />
        </div>
        <div className="profile-field">
          <label>Soyad:</label>
          <input type="text" value={userData.lastname} readOnly />
        </div>
        <div className="profile-field">
          <label>KullanÄ±cÄ± AdÄ± / E-posta:</label>
          <input type="text" value={userData.username} readOnly />
        </div>
        <div className="profile-field">
          <label>YaÅŸ:</label>
          <input type="text" value={userData.age || "-"} readOnly />
        </div>
        <div className="profile-field">
          <label>Boy (cm):</label>
          <input type="text" value={userData.height || "-"} readOnly />
        </div>
        <div className="profile-field">
          <label>Kilo (kg):</label>
          <input type="text" value={userData.weight || "-"} readOnly />
        </div>
        <div className="profile-field">
          <label>Kronik RahatsÄ±zlÄ±klar:</label>
          <input type="text" value={userData.chronic_diseases || "-"} readOnly />
        </div>
      </div>
    </div>
  );
}
