import React, { useState } from "react";
import ChatBox from "./ChatBox";
import Login from "./Login";
import Register from "./Register";
import Profile from "./Profile";
import "./App.css";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [isRegistering, setIsRegistering] = useState(false);
  const [showProfile, setShowProfile] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken("");
    setShowProfile(false);
  };

  return (
    <div className="app">
      {/* Eğer giriş yapılmamışsa Login/Register ekranı */}
      {!token ? (
        <div className="auth-container">
          <h1 className="app-title">CHATDOC 🩺</h1>
          <h2 className="app-subtitle">Sağlığınız için AI.</h2>

          {isRegistering ? (
            <>
              <Register setToken={setToken} />
              <div className="form-toggle-wrapper">
                <p>Zaten hesabın var mı?</p>
                <button onClick={() => setIsRegistering(false)}>Giriş Yap</button>
              </div>
            </>
          ) : (
            <>
              <Login setToken={setToken} />
              <div className="form-toggle-wrapper">
                <p>Hesabın yok mu?</p>
                <button onClick={() => setIsRegistering(true)}>Kayıt Ol</button>
              </div>
            </>
          )}
        </div>
      ) : (
        <>
          {/* Logo sol üstte */}
          <div className="app-logo">CHATDOC 🩺</div>

          {/* Sağ üst köşe menü */}
          <div className="top-right-menu">
            <button onClick={() => setShowProfile(!showProfile)}>
              {showProfile ? "Chat" : "Profil"}
            </button>
            <button onClick={handleLogout}>Çıkış Yap</button>
          </div>

          {/* Token varsa Chat veya Profil ekranı */}
          {showProfile ? <Profile token={token} /> : <ChatBox token={token} />}
        </>
      )}
    </div>
  );
}

export default App;
