import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Profile({ token }) {
  const [userData, setUserData] = useState({
    username: "",
    firstname: "",
    lastname: "",
    age: "",
    height: "",
    weight: "",
    chronic: ""
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(res.data);
      } catch (err) {
        console.error("Profil bilgileri alınamadı:", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.post("http://127.0.0.1:5000/profile", userData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert("Profil bilgileri başarıyla güncellendi ✅");
    } catch (err) {
      console.error("Profil kaydedilemedi:", err.response?.data || err.message);
      alert("Bir hata oluştu!");
    }
  };

  if (loading) return <p>Yükleniyor...</p>;

  return (
    <div className="profile-box">
      <h2>Profil Bilgileri</h2>

      <div className="form-group">
        <label>Ad:</label>
        <input
          type="text"
          name="firstname"
          value={userData.firstname}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Soyad:</label>
        <input
          type="text"
          name="lastname"
          value={userData.lastname}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Kullanıcı Adı / E-posta:</label>
        <input
          type="text"
          name="username"
          value={userData.username}
          disabled
        />
      </div>

      <div className="form-group">
        <label>Yaş:</label>
        <input
          type="number"
          name="age"
          value={userData.age || ""}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Boy (cm):</label>
        <input
          type="number"
          name="height"
          value={userData.height || ""}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Kilo (kg):</label>
        <input
          type="number"
          name="weight"
          value={userData.weight || ""}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Kronik Rahatsızlıklar:</label>
        <textarea
          name="chronic"
          value={userData.chronic || ""}
          onChange={handleChange}
        />
      </div>

      <button onClick={handleSave}>Kaydet</button>
    </div>
  );
}
